mapper.select:
SELECT
    APP.INSTANCE_TYPE,
    APP.CORES,
    ROUND(APP.RAM, 4) AS RAM,
    APP.PER_HOUR
FROM TASM_AZR_VM_PRICING_L APP
WHERE APP.SERIES = (
    SELECT SERIES
    FROM TASM_AZR_VM_PRICING_L
    WHERE INSTANCE_TYPE = #{instanceType}
    LIMIT 1
    )
AND APP.REGION = #{region}
AND APP.OS = #{osType}
AND APP.PER_HOUR IS NOT NULL
AND APP.CORES >= (
    SELECT CORES
    FROM TASM_AZR_VM_PRICING_L
    WHERE INSTANCE_TYPE = #{instanceType}
        LIMIT 1
) * #{discountRate}
AND APP.RAM >= (
    SELECT RAM
    FROM TASM_AZR_VM_PRICING_L
    WHERE INSTANCE_TYPE = #{instanceType}
        LIMIT 1
) * #{discountRate}
AND APP.PER_HOUR < (
    SELECT PER_HOUR
    FROM TASM_AZR_VM_PRICING_L
    WHERE INSTANCE_TYPE = #{instanceType}
        LIMIT 1
) * 1.0
ORDER BY APP.PER_HOUR * 1.0
    LIMIT 1

SELECT
    APP.INSTANCE_TYPE,
    APP.CORES,
    ROUND(APP.RAM, 4) AS RAM,
    APP.PER_HOUR
FROM TASM_AZR_VM_PRICING_L APP
WHERE APP.INSTANCE_TYPE = (
    SELECT NEW_GENTH
    FROM TASM_AZR_RSRC_OPT_MODN_L
    WHERE PREV_GENTH = #{instanceType}
      AND RGN_ID = #{region}
)
  AND APP.OS = #{osType}
  AND APP.REGION = #{region}
